import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

function App() {
  const [pageCount,setPageCount] = useState(0)
  const [coverOpen, setCoverOpen] = useState(false)

  const handleclickCover = () => {
      setCoverOpen(true)
  }

  return (
    <div class="container">
      <div class="notebook">
        <div class={`cover ${!coverOpen ? 'cover_animation': ''}`} onClick={handleclickCover}></div>
        <div class="page one">
          <p>Made with: <br />Pure CSS</p>
        </div>
        <div class="page two"></div>
        <div class="page three"></div>
      </div>
    </div>
  );
}

export default App;
